sources.map:
(function(doc){
  if('source_url' in doc) {
    emit(doc.source_url, null);
  }
  if('source_urls' in doc) {
    for(i in doc.source_urls) {
      emit(doc.source_urls[i], null);
    }
  }
  if('ids' in doc && 'url' in doc.ids) {
    emit(doc.ids['url'], null);
  }
})

journals.map:
(function(doc) {
  if(doc.citation && doc.citation.journal) {
    emit(doc.citation.journal, null);
  } else if(doc.journal) {
    emit(doc.journal, null);
  } else if(doc.categories && doc.categories.arxiv) {
    emit("arxiv:" + doc.categories.arxiv[0], null);
  }
})

ids.map:
(function(doc) {
  if(doc.ids) {
    for(var i in doc.ids) { 
       emit(i + ':' + doc.ids[i], null); 
    }
  } 
})
