rescrape.map:
function(doc) {
  if(doc.rescrape && doc.scraper_module ) { 
    emit(doc.scraper_module, null); 
  } else if (doc.rescrape) {
    emit("Scraper Module Missing", null);
  }
}

scraper_total_docs.map:
function(doc) {
  if(doc.scraper_module) {
    emit(doc.scraper_module, 1);
  } else {
    emit("Scraper Module Missing", 1);
  }
}

scraper_exception_error_count.map:
function(doc) {
  if(doc.rescrape && doc.scraper_module && doc.exception) {
    emit([doc.scraper_module, doc.exception], 1);
  } else if (doc.rescrape && doc.scraper_module) {
    emit([doc.scraper_module, "Exception Details Missing"], 1);
  } else if (doc.rescrape && doc.exception) {
    emit(["Scraper Module Missing", doc.exception], 1);
  } else if (doc.rescrape) {
    emit(["Scraper Module Missing", "Exception Details Missing"], 1);
  }
}

scraper_exception_errors.map:
function(doc) {
  if(doc.rescrape && doc.scraper_module && doc.exception) {
    emit([doc.scraper_module, doc.exception], null);
  } else if (doc.rescrape && doc.scraper_module) {
    emit([doc.scraper_module, "Exception Details Missing"], null);
  } else if (doc.rescrape && doc.exception) {
    emit(["Scraper Module Missing", doc.exception], null);
  } else if (doc.rescrape) {
    emit(["Scraper Module Missing", "Exception Details Missing"], null);
  }
}
